// src/components/ArticleCard.js

import React from 'react';
import { Link } from 'react-router-dom';

const ArticleCard = ({ id, title, excerpt, imageUrl }) => {
  return (
    <div className="bg-white rounded-lg shadow-lg overflow-hidden transform hover:-translate-y-2 transition-transform duration-300 ease-in-out">
      <Link to={`/article/${id}`}>
        <img 
          className="w-full h-48 object-cover" 
          src={imageUrl || 'https://via.placeholder.com/400x250?text=Health+Image'} // Ảnh mặc định
          alt={title} 
        />
        <div className="p-6">
          <h3 className="font-bold text-xl mb-3 text-gray-800 hover:text-blue-600">{title}</h3>
          <p className="text-gray-600 text-base">{excerpt}</p>
          <span className="text-blue-500 font-semibold mt-4 inline-block">
            Xem toàn bộ →
          </span>
        </div>
      </Link>
    </div>
  );
};

export default ArticleCard;
