// src/pages/HomePage.js
import React from 'react';
import data from '../data.json';
import ArticleCard from '../components/ArticleCard';
import { FaSearch } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext'; // <-- 1. Import hook

const HomePage = () => {
    const articles = data.articles;
    const { user } = useAuth(); // <-- 2. Lấy thông tin user

    return (
        <div className="space-y-8">
            {/* 3. Thêm lời chào nếu đã đăng nhập */}
            {user && (
              <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md">
                <h2 className="text-2xl font-bold text-gray-800 dark:text-white">
                  Chào mừng trở lại, {user.name}!
                </h2>
                <p className="text-gray-600 dark:text-gray-300 mt-2">
                  Hãy cùng khám phá những thông tin y khoa mới nhất nhé.
                </p>
              </div>
            )}

            {/* Thanh tìm kiếm */}
            <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md">
              {/* ... code thanh tìm kiếm ... */}
            </div>

            {/* Danh sách bài viết */}
            <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md">
              {/* ... code danh sách bài viết ... */}
            </div>
        </div>
    );
};

export default HomePage;
