// src/App.js
import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import { useTheme } from './context/ThemeContext';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import ProtectedRoute from './components/ProtectedRoute';
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import ContactPage from './pages/ContactPage';
import LoginPage from './pages/LoginPage';
import ArticleDetailPage from './pages/ArticleDetailPage';
import ProfilePage from './pages/ProfilePage';
import CreatePostPage from './pages/CreatePostPage';

function App() {
  const { themeName } = useTheme();
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  const themeClass = themeName === 'dark' ? 'theme-dark' : '';

  return (
    <div className={themeClass}>
      <Navbar setSearchTerm={setSearchTerm} />

      {/* Vùng chứa nội dung chính, tương đương với .app-main */}
      <div className="app-content-wrapper container mx-auto flex flex-col md:flex-row gap-8 px-4 py-8">
        <Sidebar setSelectedCategory={setSelectedCategory} />
        <main className="flex-grow w-full md:w-auto">
          <Routes>
            <Route path="/" element={<HomePage selectedCategory={selectedCategory} searchTerm={searchTerm} />} />
            <Route path="/article/:id" element={<ArticleDetailPage />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/contact" element={<ContactPage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/profile" element={<ProtectedRoute><ProfilePage /></ProtectedRoute>} />
            <Route path="/create-post" element={<ProtectedRoute><CreatePostPage /></ProtectedRoute>} />
          </Routes>
        </main>
      </div>
    </div>
  );
}

export default App;
