import React, { useEffect, useState } from 'react';
import api from '../services/api';

export default function Home() {
  const [news, setNews] = useState([]);

  useEffect(() => {
    api.get('/news').then((res) => setNews(res.data));
  }, []);

  return (
    <div className="p-6">
      <h2 className="text-2xl font-bold mb-4">Tin tức y tế mới nhất</h2>
      {news.map((n) => (
        <div key={n.id} className="border-b py-3">
          <h3 className="text-xl font-semibold">{n.title}</h3>
          <p>{n.content}</p>
        </div>
      ))}
    </div>
  );
}
