// src/components/CommentSection.js
import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';

const CommentSection = ({ articleId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const { user } = useAuth();

    useEffect(() => {
        api.get(`/comments/${articleId}`).then(res => setComments(res.data));
    }, [articleId]);

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!newComment.trim()) return;
        try {
            const res = await api.post(`/comments/${articleId}`, { comment: newComment });
            setComments([res.data, ...comments]);
            setNewComment('');
        } catch (error) {
            alert('Bạn cần đăng nhập để bình luận.');
        }
    };

    return (
        <div className="mt-12">
            <h2 className="text-2xl font-bold mb-6 text-main">Bình luận</h2>
            {user ? (
                <form onSubmit={handleSubmit} className="mb-8">
                    <textarea value={newComment} onChange={e => setNewComment(e.target.value)} placeholder="Viết bình luận của bạn..." rows="4" className="w-full p-3 rounded-md border border-main bg-transparent"/>
                    <button type="submit" className="mt-2 bg-accent bg-accent-hover text-white font-bold py-2 px-5 rounded-lg">Gửi</button>
                </form>
            ) : (
                <p className="text-subtle mb-8">Vui lòng <Link to="/login" className="text-accent underline">đăng nhập</Link> để bình luận.</p>
            )}
            <div className="space-y-6">
                {comments.map(comment => (
                    <div key={comment.id} className="flex gap-4">
                        <img src={comment.avatar} alt={comment.name} className="w-10 h-10 rounded-full"/>
                        <div>
                            <p className="font-semibold text-main">{comment.name}</p>
                            <p className="text-sm text-subtle">{new Date(comment.created_at).toLocaleString()}</p>
                            <p className="mt-2 text-main">{comment.comment}</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};
export default CommentSection;
