import React from 'react';
// Import dữ liệu trực tiếp từ file JSON
import data from '../data.json';
import ArticleCard from '../components/ArticleCard';

const HomePage = () => {
    // Lấy danh sách bài viết từ file JSON đã import
    const articles = data.articles;

    return (
        <main className="app-main">
            {/* Phần giới thiệu */}
            <section className="about-careplus container">
                <h2>Kiến thức Y khoa</h2>
                <p>Cập nhật những thông tin sức khỏe mới nhất và hữu ích.</p>
            </section>

            {/* Phần danh sách bài viết */}
            <section className="article-list-section container">
                {articles.length > 0 ? (
                    <div className="article-grid">
                        {articles.map(article => (
                            <ArticleCard
                                key={article.id}
                                id={article.id}
                                title={article.title}
                                excerpt={article.excerpt}
                                imageUrl={article.imageUrl}
                                author={article.author}
                                date={article.date}
                            />
                        ))}
                    </div>
                ) : (
                    <p className="no-articles-message">Chưa có bài viết nào.</p>
                )}
            </section>
        </main>
    );
};

export default HomePage;
