// src/components/ChatbotWidget.js
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../context/AuthContext';
import { FaComments, FaTimes, FaPaperPlane } from 'react-icons/fa'; // <-- LỖI ĐÃ ĐƯỢC SỬA Ở ĐÂY
import api from '../services/api';

const BOT_AVATAR = 'https://maunailxinh.com/wp-content/uploads/2025/06/avatar-bua-troll-6.jpg';

const ChatbotWidget = () => {
  const { user } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [quickReplies, setQuickReplies] = useState([]);
  const [input, setInput] = useState('');
  const [selectedTopicId, setSelectedTopicId] = useState(null);
  const chatEndRef = useRef(null);

  useEffect(() => {
    if (isOpen && messages.length === 0) {
      const initialMessage = `Chào ${user ? user.name.split(' ')[0] : 'bạn'}! Hãy cho tôi biết triệu chứng bạn đang gặp phải (ví dụ: "tôi bị đau đầu").`;
      setMessages([{ type: 'bot', text: initialMessage }]);
    }
  }, [isOpen, user, messages.length]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, quickReplies]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim() || quickReplies.length > 0) return;

    const userMessage = { type: 'user', text: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    try {
      const res = await api.post('/chatbot/interact', { message: input });
      const botResponse = res.data;

      if (botResponse.type === 'options') {
          setMessages(prev => [...prev, { type: 'bot', text: botResponse.question }]);
          setQuickReplies(Object.entries(botResponse.options).map(([id, text]) => ({ id, text })));
          setSelectedTopicId(botResponse.topicId);
      } else {
          setMessages(prev => [...prev, { type: 'bot', text: botResponse.answer }]);
      }
    } catch (error) {
      setMessages(prev => [...prev, { type: 'bot', text: 'Đã có lỗi xảy ra, vui lòng thử lại.' }]);
    }
  };

  const handleQuickReplyClick = async (reply) => {
      setMessages(prev => [...prev, { type: 'user', text: reply.text }]);
      setQuickReplies([]);

      try {
          const res = await api.post('/chatbot/answer', { topicId: selectedTopicId, optionId: reply.id });
          const escalation = "Thông tin trên chỉ mang tính tham khảo. Nếu triệu chứng không thuyên giảm, bạn nên đi khám bác sĩ. Bạn cần hỗ trợ gì khác không?";
          setMessages(prev => [...prev, { type: 'bot', text: res.data.answer }, { type: 'bot', text: escalation }]);
      } catch (error) {
          console.error("Lỗi khi lấy câu trả lời:", error);
      }
  };

  return (
    <>
      <button onClick={() => setIsOpen(!isOpen)} className="fixed bottom-24 right-5 bg-accent w-16 h-16 rounded-full text-white flex items-center justify-center shadow-lg transform transition-transform hover:scale-110 z-50">
        {isOpen ? <FaTimes size={24} /> : <FaComments size={24} />}
      </button>

      {isOpen && (
        <div className="fixed bottom-5 right-5 w-[calc(100%-2.5rem)] max-w-sm h-[70vh] max-h-[500px] card-bg rounded-lg shadow-xl flex flex-col z-50">
          <div className="p-4 border-b border-main flex items-center gap-3 flex-shrink-0">
            <img src={BOT_AVATAR} alt="bot avatar" className="w-10 h-10 rounded-full" />
            <h3 className="font-bold text-main">Trợ lý sức khỏe CarePlus</h3>
          </div>
          
          <div className="flex-grow p-4 overflow-y-auto space-y-4">
            {messages.map((msg, index) => (
              <div key={index} className={`flex items-start gap-3 ${msg.type === 'user' ? 'flex-row-reverse' : ''}`}>
                {msg.type === 'bot' && <img src={BOT_AVATAR} alt="bot" className="w-8 h-8 rounded-full flex-shrink-0"/>}
                <div className={`p-3 rounded-lg max-w-[80%] ${msg.type === 'user' ? 'bg-accent text-white' : 'bg-gray-100 text-gray-800'}`}>
                  {msg.text}
                </div>
              </div>
            ))}
            {quickReplies.length > 0 && (
                <div className="flex justify-end pt-2">
                    <div className="space-y-2 max-w-[85%] w-full">
                        {quickReplies.map(reply => (
                            <button key={reply.id} onClick={() => handleQuickReplyClick(reply)} className="block w-full text-center p-2 rounded-full border border-accent bg-white text-accent font-semibold hover:bg-blue-50 transition-colors">
                                {reply.text}
                            </button>
                        ))}
                    </div>
                </div>
            )}
            <div ref={chatEndRef} />
          </div>

          <form onSubmit={handleSend} className="p-4 border-t border-main flex gap-2 flex-shrink-0">
            <input 
              value={input} 
              onChange={e => setInput(e.target.value)} 
              type="text" 
              placeholder="Nhập triệu chứng..." 
              className="flex-grow p-3 rounded-md border border-main bg-transparent text-main"
              disabled={quickReplies.length > 0}
            />
            <button type="submit" className="bg-accent p-3 rounded-md text-white" disabled={quickReplies.length > 0}>
              <FaPaperPlane />
            </button>
          </form>
        </div>
      )}
    </>
  );
};
export default ChatbotWidget;
