// src/components/ChatbotWidget.js
import React, { useState, useEffect, useRef } from 'react';
// import api from '../services/api'; // <-- DÒNG NÀY ĐÃ ĐƯỢC XÓA
import { FaComments, FaTimes, FaPaperPlane } from 'react-icons/fa';

const ChatbotWidget = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([{ type: 'bot', text: 'Chào bạn, tôi có thể giúp gì về sức khỏe cho bạn?' }]);
  const [input, setInput] = useState('');
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = { type: 'user', text: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    setTimeout(() => {
      let botResponse = 'Xin lỗi, tôi chưa hiểu câu hỏi của bạn.';
      if (input.toLowerCase().includes('đau đầu')) {
        botResponse = 'Đau đầu có nhiều nguyên nhân. Bạn có thể cho tôi biết bạn đau ở vùng nào không? Ví dụ: đau hai bên thái dương, đau sau gáy, hay đau vùng trán?';
      } else if (input.toLowerCase().includes('mất ngủ')) {
        botResponse = 'Mất ngủ ảnh hưởng rất nhiều đến chất lượng cuộc sống. Bạn khó đi vào giấc ngủ, hay thường xuyên thức giấc giữa đêm?';
      }
      const botMessage = { type: 'bot', text: botResponse };
      setMessages(prev => [...prev, botMessage]);
    }, 1000);
  };

  return (
    <>
      <button onClick={() => setIsOpen(!isOpen)} className="fixed bottom-24 right-5 bg-accent w-16 h-16 rounded-full text-white flex items-center justify-center shadow-lg transform transition-transform hover:scale-110 z-50">
        {isOpen ? <FaTimes size={24} /> : <FaComments size={24} />}
      </button>

      {isOpen && (
        <div className="fixed bottom-5 right-5 w-[calc(100%-2.5rem)] max-w-sm h-[70vh] max-h-[500px] card-bg rounded-lg shadow-xl flex flex-col z-50">
          <div className="p-4 border-b border-main"><h3 className="font-bold text-main">Tư vấn sức khỏe</h3></div>
          
          <div className="flex-grow p-4 overflow-y-auto space-y-4">
            {messages.map((msg, index) => (
              <div key={index} className={`flex ${msg.type === 'user' ? 'justify-end' : 'justify-start'}`}>
                <p className={`p-3 rounded-lg max-w-[80%] ${msg.type === 'user' ? 'bg-accent text-white' : 'bg-gray-200 dark:bg-gray-700 text-main'}`}>
                  {msg.text}
                </p>
              </div>
            ))}
            <div ref={chatEndRef} />
          </div>

          <form onSubmit={handleSend} className="p-4 border-t border-main flex gap-2">
            <input value={input} onChange={e => setInput(e.target.value)} type="text" placeholder="Nhập câu hỏi của bạn..." className="flex-grow p-2 rounded-md border border-main bg-transparent text-main"/>
            <button type="submit" className="bg-accent p-3 rounded-md text-white"><FaPaperPlane /></button>
          </form>
        </div>
      )}
    </>
  );
};

export default ChatbotWidget;
