// src/App.js

// --- CODE CŨ GIỮ NGUYÊN: Import các thư viện và component cần thiết ---
import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import { useTheme } from './context/ThemeContext';

// Import các component chung
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import ProtectedRoute from './components/ProtectedRoute';
import ChatbotWidget from './components/ChatbotWidget';
import { FaFacebook } from 'react-icons/fa';

// Import các trang (pages)
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import ContactPage from './pages/ContactPage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import ArticleDetailPage from './pages/ArticleDetailPage';
import ProfilePage from './pages/ProfilePage';
import CreatePostPage from './pages/CreatePostPage';
import ForgotPasswordPage from './pages/ForgotPasswordPage';
import KnowledgeHubPage from './pages/KnowledgeHubPage';
import KnowledgeDetailPage from './pages/KnowledgeDetailPage';
import QandAPage from './pages/QandAPage';
import AskQuestionPage from './pages/AskQuestionPage';
import QuestionDetailPage from './pages/QuestionDetailPage';
import MessengerPage from './pages/MessengerPage'; // <-- CODE MỚI: Import trang Messenger

function App() {
  // --- CODE CŨ GIỮ NGUYÊN: Quản lý state cho tìm kiếm và theme ---
  const [searchTerm, setSearchTerm] = useState('');
  const { themeName } = useTheme();
  const themeClass = themeName === 'dark' ? 'theme-dark' : '';

  return (
    // --- CODE CŨ GIỮ NGUYÊN: Cấu trúc layout chính ---
    <div className={`flex flex-col min-h-screen ${themeClass}`}>
      <Navbar setSearchTerm={setSearchTerm} />
      <main className="flex-grow container mx-auto px-4 py-8">
          <Routes>
            {/* --- CODE CŨ GIỮ NGUYÊN: Các Route cơ bản --- */}
            <Route path="/" element={<HomePage searchTerm={searchTerm} />} />
            <Route path="/article/:id" element={<ArticleDetailPage />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/contact" element={<ContactPage />} />

            {/* --- CODE CŨ GIỮ NGUYÊN: Các Route xác thực --- */}
            <Route path="/login" element={<LoginPage />} />
            <Route path="/register" element={<RegisterPage />} />
            <Route path="/forgot-password" element={<ForgotPasswordPage />} />

            {/* --- CODE MỚI: Các Route cho tính năng Kho Kiến thức --- */}
            <Route path="/knowledge" element={<KnowledgeHubPage />} />
            <Route path="/knowledge/:id" element={<KnowledgeDetailPage />} />

            {/* --- CODE MỚI: Các Route cho tính năng Hỏi & Đáp (Vấn đáp) --- */}
            <Route path="/q-a" element={<QandAPage />} />
            <Route path="/question/:id" element={<QuestionDetailPage />} />
            <Route path="/ask-question" element={<ProtectedRoute><AskQuestionPage /></ProtectedRoute>} />

            {/* --- CODE MỚI: Route cho trang Messenger (Tư vấn AI) --- */}
            <Route path="/messenger" element={<ProtectedRoute><MessengerPage /></ProtectedRoute>} />

            {/* --- CODE CŨ GIỮ NGUYÊN: Các Route được bảo vệ khác --- */}
            <Route path="/profile" element={<ProtectedRoute><ProfilePage /></ProtectedRoute>} />
            <Route path="/create-post" element={<ProtectedRoute><CreatePostPage /></ProtectedRoute>} />
          </Routes>
      </main>

      {/* --- CODE CŨ GIỮ NGUYÊN: Các thành phần nổi cố định --- */}
      <div className="fixed bottom-5 right-5 flex flex-col items-center gap-4 z-40">
        <a 
          href="https://www.facebook.com/trien.vo.337115/about_overview" 
          target="_blank" 
          rel="noopener noreferrer"
          className="bg-[#1877F2] w-14 h-14 rounded-full text-white flex items-center justify-center shadow-lg hover:scale-110 transition-transform"
          aria-label="Facebook"
        >
          <FaFacebook size={28} />
        </a>
      </div>
      <ChatbotWidget />

      <Footer />
    </div>
  );
}

export default App;
