// src/pages/KnowledgeHubPage.js
import React from 'react';
import { Link } from 'react-router-dom';
import { medicalKnowledgeBase } from '../knowledgeBase'; // Import dữ liệu kiến thức

const KnowledgeHubPage = () => {
  return (
    <div className="card-bg p-6 md:p-10 rounded-lg">
      <h1 className="text-4xl font-bold mb-8 text-main border-b-2 border-accent pb-4">
        Kho Kiến thức Y khoa
      </h1>
      <div className="space-y-8">
        {medicalKnowledgeBase.map((category) => (
          <section key={category.id}>
            <h2 className="text-2xl font-semibold mb-4 text-main">{category.title}</h2>
            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
              {category.subitems.map((item) => (
                <Link 
                  to={`/knowledge/${item.id}`} 
                  key={item.id}
                  className="block p-4 border border-main rounded-md text-subtle hover:bg-secondary hover:border-accent hover:text-accent transition-colors"
                >
                  {item.name}
                </Link>
              ))}
            </div>
          </section>
        ))}
      </div>
    </div>
  );
};

export default KnowledgeHubPage;
