// src/components/ServicesSection.js
import React from 'react';
import { FaBookMedical, FaQuestionCircle, FaComments } from 'react-icons/fa';

const services = [
  { icon: <FaBookMedical size={40} />, title: "Kho Kiến thức", description: "Tra cứu thông tin chi tiết về bệnh, thuốc và các phương pháp điều trị." },
  { icon: <FaQuestionCircle size={40} />, title: "Hỏi & Đáp Sức khỏe", description: "Đặt câu hỏi và nhận câu trả lời từ cộng đồng và các chuyên gia." },
  { icon: <FaComments size={40} />, title: "Tư vấn AI", description: "Trợ lý ảo cung cấp thông tin ban đầu dựa trên triệu chứng của bạn." },
];

const ServicesSection = () => {
  return (
    <section className="py-16 bg-secondary">
      <div className="container mx-auto px-4 text-center">
        <h2 className="text-3xl font-bold mb-10 text-main">Các tính năng chính</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
          {services.map((service, index) => (
            <div key={index} className="p-8 bg-primary rounded-lg shadow-md">
              <div className="text-accent mb-4 inline-block">{service.icon}</div>
              <h3 className="text-xl font-bold mb-2 text-main">{service.title}</h3>
              <p className="text-subtle">{service.description}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};
export default ServicesSection;
