import React, { useState, useEffect } from 'react';
// Dòng import này bây giờ sẽ hoạt động vì 'getArticles' đã tồn tại trong file api.js
import { getArticles } from '../services/api';

const HomePage = () => {
    const [articles, setArticles] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchArticles = async () => {
            try {
                // Sử dụng trực tiếp hàm getArticles đã import
                const response = await getArticles();
                setArticles(response.data);
            } catch (error) {
                console.error("Lỗi khi tải bài viết:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchArticles();
    }, []);

    if (loading) {
        return <div className="p-10 text-center">Đang tải...</div>;
    }

    return (
        <div className="container mx-auto p-4">
            <h1 className="text-3xl font-bold my-6">Bài viết mới nhất</h1>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                {articles.map(article => (
                    <div key={article.id} className="bg-white p-6 rounded-lg shadow-md">
                        <h2 className="text-xl font-bold mb-2">{article.title}</h2>
                        <p className="text-gray-700">{article.content.substring(0, 150)}...</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default HomePage;
