// src/App.js

import React from 'react';
import { Routes, Route } from 'react-router-dom';

// Import các hook cần thiết (CHỈ MỘT LẦN)
import { useTheme } from './context/ThemeContext';

// Import các component chung
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import ProtectedRoute from './components/ProtectedRoute';

// Import các trang (pages)
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import ContactPage from './pages/ContactPage';
import LoginPage from './pages/LoginPage';
import CreatePostPage from './pages/CreatePostPage';
import ProfilePage from './pages/ProfilePage';

function App() {
  // Đổi tên biến ở đây để khớp với ThemeContext mới
  const { themeName } = useTheme();

  return (
    // Sử dụng class theme tương ứng
    <div className={`app-container theme-${themeName} bg-gray-100 dark:bg-gray-900 min-h-screen transition-colors duration-300`}>
      <Navbar />
      <div className="container mx-auto flex flex-col md:flex-row gap-8 px-4 py-8">
        <Sidebar />
        <main className="flex-grow w-full md:w-auto">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/contact" element={<ContactPage />} />
            <Route path="/login" element={<LoginPage />} />

            <Route 
              path="/profile" 
              element={
                <ProtectedRoute>
                  <ProfilePage />
                </ProtectedRoute>
              } 
            />

            <Route
              path="/create-post"
              element={
                <ProtectedRoute>
                  <CreatePostPage />
                </ProtectedRoute>
              }
            />
          </Routes>
        </main>
      </div>
    </div>
  );
}

export default App;
