import React from 'react';
import { getUser, logout } from '../services/auth';

export default function Navbar() {
  const user = getUser();

  return (
    <nav className="bg-blue-600 text-white flex justify-between items-center p-4">
      <h1 className="text-xl font-bold">DemoCarePlus</h1>
      <div>
        {user ? (
          <div className="flex items-center gap-3">
            <img src={user.avatar} alt="avatar" className="w-8 h-8 rounded-full" />
            <span>{user.name}</span>
            <button onClick={logout} className="ml-2 bg-red-500 px-3 py-1 rounded">Đăng xuất</button>
          </div>
        ) : (
          <a href="/login" className="bg-white text-blue-600 px-4 py-2 rounded">Đăng nhập</a>
        )}
      </div>
    </nav>
  );
}
