// src/components/Sidebar.js
import React, { useState } from 'react';
import { medicalCategories } from '../categories';
import { Link } from 'react-router-dom';
import { FaChevronDown } from 'react-icons/fa'; // Icon mũi tên

// Component con cho từng mục danh bạ
const CollapsibleCategory = ({ title, subitems }) => {
  const [isOpen, setIsOpen] = useState(true); // Mặc định mở

  return (
    <li className="mb-4">
      <button
        onClick={() => setIsOpen(!isOpen)}
        className="w-full flex justify-between items-center font-semibold text-gray-700 dark:text-gray-200 hover:text-blue-600 dark:hover:text-blue-400 transition-colors"
      >
        <span>{title}</span>
        <FaChevronDown 
          className={`transform transition-transform duration-300 ${isOpen ? 'rotate-180' : 'rotate-0'}`} 
        />
      </button>
      {/* Nội dung thu gọn */}
      <div 
        className={`overflow-hidden transition-all duration-300 ease-in-out ${isOpen ? 'max-h-screen mt-2' : 'max-h-0'}`}
      >
        <ul className="ml-4 space-y-2 border-l border-gray-200 dark:border-gray-700">
          {subitems.map((item, subIndex) => (
            <li key={subIndex} className="pl-3">
              <Link 
                to={`/category/${item.toLowerCase().replace(/ /g, '-')}`}
                className="block text-gray-600 dark:text-gray-400 hover:text-blue-600 dark:hover:text-blue-400"
              >
                {item}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </li>
  );
};

// Component Sidebar chính
const Sidebar = () => {
  return (
    <aside className="w-full md:w-64 lg:w-72 flex-shrink-0 bg-white dark:bg-gray-800 shadow-lg p-6 rounded-lg self-start sticky top-24">
      <h2 className="text-xl font-bold mb-4 text-gray-800 dark:text-white border-b border-gray-300 dark:border-gray-700 pb-2">
        Danh mục Y tế
      </h2>
      <nav>
        <ul>
          {medicalCategories.map((category, index) => (
            <CollapsibleCategory
              key={index}
              title={category.title}
              subitems={category.subitems}
            />
          ))}
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
