// src/components/Navbar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import { FaSun, FaMoon } from 'react-icons/fa';

const Navbar = () => {
  const { user, logout } = useAuth();
  const { themeName, toggleTheme } = useTheme();

  return (
    <header className="card-bg border-b border-main sticky top-0 z-50">
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        {/* Logo & Tên trang */}
        <Link to="/" className="flex items-center gap-3">
        <Link to="/create-post" className="font-medium text-main text-accent-hover">Đăng bài</Link> {/* <-- NÚT ĐĂNG BÀI */}
          <img src="https://ttagencyads.com/wp-content/uploads/2022/03/Logo-rong-do.jpg" alt="CarePlus Logo" className="w-10 h-10 rounded-full object-cover"/>
          <div><h1 className="text-xl font-bold text-accent">CarePlus</h1></div>
        </Link>
        
        {/* Nav Links */}
        <nav className="hidden md:flex items-center gap-6">
          <Link to="/" className="font-medium text-main text-accent-hover">Trang chủ</Link>
          <Link to="/about" className="font-medium text-main text-accent-hover">Về chúng tôi</Link>
          <Link to="/contact" className="font-medium text-main text-accent-hover">Liên hệ</Link>
        </nav>

        {/* Actions */}
        <div className="flex items-center gap-4">
          <button onClick={toggleTheme} className="p-2 rounded-full text-subtle text-accent-hover" title="Đổi giao diện">
            {themeName === 'light' ? <FaMoon size={18} /> : <FaSun size={18} />}
          </button>
          
          {user ? (
            <div className="flex items-center gap-3">
              <Link to="/profile" className="flex items-center gap-3">
                <span className="font-semibold text-main hidden sm:inline">{user.name}</span>
                <img src={user.avatar} alt="User Avatar" className="w-9 h-9 rounded-full border-2 border-accent"/>
              </Link>
              <button onClick={logout} className="bg-red-500 text-white px-3 py-1.5 rounded-md font-medium hover:bg-red-600 text-sm">Thoát</button>
            </div>
          ) : (
            <a href="http://localhost:3001/api/auth/google" className="bg-accent bg-accent-hover text-white px-4 py-2 rounded-md font-medium transition-colors">
              Đăng nhập
            </a>
          )}
        </div>
      </div>
    </header>
  );
};

export default Navbar;
