// src/pages/HomePage.js
import React from 'react';
import { Link } from 'react-router-dom';
import data from '../data.json';
import ArticleCard from '../components/ArticleCard';

const HomePage = ({ selectedCategory, searchTerm }) => {
  // Logic lọc bài viết giữ nguyên
  const filteredArticles = data.articles
    .filter(article => 
      selectedCategory === 'all' || article.category === selectedCategory
    )
    .filter(article => 
      !searchTerm ||
      article.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      article.excerpt.toLowerCase().includes(searchTerm.toLowerCase())
    );

  return (
    <div className="space-y-12">
      {/* Section 1: Danh sách bài viết */}
      <section className="article-list-section">
        <h2 className="text-3xl font-bold mb-6 pb-3 border-b-2 border-accent inline-block text-main">
          {searchTerm ? `Kết quả cho "${searchTerm}"` : 'Bài viết mới nhất'}
        </h2>
        
        {filteredArticles.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {filteredArticles.map(article => (
              <ArticleCard key={article.id} article={article} />
            ))}
          </div>
        ) : (
          <div className="card-bg p-8 rounded-lg text-center">
            <p className="text-subtle">Không tìm thấy bài viết nào phù hợp.</p>
          </div>
        )}
      </section>

      {/* Section 2: Giới thiệu về CarePlus */}
      <section className="about-careplus-section card-bg p-8 rounded-lg">
        <h2 className="text-2xl font-bold mb-4 text-accent">Về CarePlus</h2>
        <p className="text-subtle leading-relaxed mb-6">
          Hệ thống Phòng khám quốc tế CarePlus được điều hành bởi Công ty TNHH CityClinic Việt Nam. Những thông tin y khoa trên trang web chỉ dùng để tham khảo, bạn nên liên hệ bác sĩ để được tư vấn trước khi có bất cứ hành động nào.
        </p>
        <Link to="/about" className="font-semibold text-accent text-accent-hover">
          Đọc thêm về chúng tôi &rarr;
        </Link>
      </section>
    </div>
  );
};

export default HomePage;
