// src/App.js
import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import { useTheme } from './context/ThemeContext';

// Import components
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import ProtectedRoute from './components/ProtectedRoute';

// Import pages
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import ContactPage from './pages/ContactPage';
import LoginPage from './pages/LoginPage';
import ArticleDetailPage from './pages/ArticleDetailPage';
import ProfilePage from './pages/ProfilePage';

function App() {
  const { themeName } = useTheme();
  // State để quản lý bộ lọc và tìm kiếm, được đặt ở cấp cao nhất
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');

  // Class theme sẽ được áp dụng cho toàn bộ ứng dụng
  const themeClass = themeName === 'dark' ? 'theme-dark' : '';

  return (
    <div className={themeClass}>
      {/* Truyền hàm setSearchTerm để Navbar có thể cập nhật từ khóa tìm kiếm */}
      <Navbar setSearchTerm={setSearchTerm} />

      <div className="container mx-auto flex flex-col md:flex-row gap-8 px-4 py-8">
        {/* Truyền hàm setSelectedCategory để Sidebar có thể lọc bài viết */}
        <Sidebar setSelectedCategory={setSelectedCategory} />

        <main className="flex-grow w-full md:w-auto">
          {/* Khối Routes để định nghĩa các trang */}
          <Routes>
            {/* Truyền state lọc và tìm kiếm vào HomePage */}
            <Route 
              path="/" 
              element={<HomePage selectedCategory={selectedCategory} searchTerm={searchTerm} />} 
            />
            <Route path="/article/:id" element={<ArticleDetailPage />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/contact" element={<ContactPage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route 
              path="/profile" 
              element={
                <ProtectedRoute>
                  <ProfilePage />
                </ProtectedRoute>
              } 
            />
          </Routes>
        </main>
      </div>
    </div>
  );
}

export default App;
