// src/context/ThemeContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';

// Không cần export 'themes' vì component sẽ chỉ dùng tên
const ThemeContext = createContext({
  themeName: 'light', // Sẽ là 'light', 'dark', hoặc 'green'
  toggleTheme: () => {},
});

export const useTheme = () => useContext(ThemeContext);

export const ThemeProvider = ({ children }) => {
  const [themeName, setThemeName] = useState('light');

  useEffect(() => {
    // Lấy theme đã lưu từ localStorage khi tải trang
    const savedTheme = localStorage.getItem('app-theme');
    if (savedTheme) {
      setThemeName(savedTheme);
    }
  }, []);

  useEffect(() => {
    // Lưu theme vào localStorage mỗi khi thay đổi
    localStorage.setItem('app-theme', themeName);
    // Chúng ta sẽ không áp dụng vào document.body
    // mà App.js sẽ tự lấy themeName và áp dụng vào div gốc
  }, [themeName]);

  const toggleTheme = () => {
    setThemeName(prevThemeName => {
      if (prevThemeName === 'light') return 'dark';
      if (prevThemeName === 'dark') return 'green';
      if (prevThemeName ==="green") return 'light';
      return 'light'; // Mặc định
    });
  };

  return (
    <ThemeContext.Provider value={{ themeName, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
};
