// src/components/Navbar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
// Import các icon cần thiết
import { FaSun, FaMoon, FaLeaf, FaGlobe, FaUserCircle } from 'react-icons/fa';

const Navbar = () => {
  const { user, logout } = useAuth();
  const { theme, setTheme } = useTheme();

  return (
    <header className="bg-white dark:bg-gray-800 shadow-md sticky top-0 z-50 transition-colors duration-300">
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        
        {/* Phần 1: Logo & Tên trang */}
        <div className="flex items-center gap-3">
          <Link to="/" className="flex items-center gap-2">
            {/* Bạn có thể thay <img> bằng logo thật sự */}
            <img 
              src="https://placehold.co/40x40/0077b6/FFF?text=C+" 
              alt="CarePlus Logo" 
              className="w-10 h-10 rounded-full"
            />
            <div>
              <h1 className="text-xl font-bold text-blue-600 dark:text-blue-400">CarePlus</h1>
              <span className="text-xs text-gray-500 dark:text-gray-400 hidden sm:block">
                Thông tin & Kiến thức Y khoa
              </span>
            </div>
          </Link>
        </div>

        {/* Phần 2: Thanh điều hướng (NavBar) */}
        <nav className="hidden md:flex items-center gap-6">
          <Link 
            to="/" 
            className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600 dark:hover:text-blue-400 border-b-2 border-transparent hover:border-blue-600 dark:hover:border-blue-400 transition-all pb-1"
          >
            Trang chủ
          </Link>
          <Link 
            to="/about" 
            className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600 dark:hover:text-blue-400 border-b-2 border-transparent hover:border-blue-600 dark:hover:border-blue-400 transition-all pb-1"
          >
            Về chúng tôi
          </Link>
          <Link 
            to="/" 
            className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600 dark:hover:text-blue-400 border-b-2 border-transparent hover:border-blue-600 dark:hover:border-blue-400 transition-all pb-1"
          >
            Kiến thức
          </Link>
          <Link 
            to="/contact" 
            className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600 dark:hover:text-blue-400 border-b-2 border-transparent hover:border-blue-600 dark:hover:border-blue-400 transition-all pb-1"
          >
            Liên hệ
          </Link>
        </nav>

        {/* Phần 3: Các nút hành động (Actions) */}
        <div className="flex items-center gap-3">
          {/* Theme Switcher */}
          <button
            onClick={() => setTheme(theme === 'theme-light' ? 'theme-dark' : theme === 'theme-dark' ? 'theme-green' : 'theme-light')}
            className="p-2 rounded-full text-gray-700 dark:text-gray-200 hover:bg-gray-200 dark:hover:bg-gray-700"
            title="Đổi giao diện"
          >
            {theme === 'theme-light' && <FaMoon />}
            {theme === 'theme-dark' && <FaLeaf />}
            {theme === 'theme-green' && <FaSun />}
          </button>
          
          {/* Language Toggle (Placeholder) */}
          <button 
            className="p-2 rounded-full text-gray-700 dark:text-gray-200 hover:bg-gray-200 dark:hover:bg-gray-700 hidden sm:block"
            title="Ngôn ngữ"
          >
            <FaGlobe />
          </button>

          {/* Login / User Info */}
          {user ? (
            <div className="flex items-center gap-3">
              <span className="font-semibold text-gray-700 dark:text-gray-200 hidden sm:inline">
                Chào, {user.name.split(' ')[0]}!
              </span>
              <button 
                onClick={logout} 
                className="bg-red-500 text-white px-4 py-2 rounded-md font-medium hover:bg-red-600 transition-colors"
              >
                Đăng xuất
              </button>
            </div>
          ) : (
            <a
              href="http://localhost:3001/api/auth/google"
              className="bg-blue-600 text-white px-4 py-2 rounded-md font-medium hover:bg-blue-700 transition-colors"
            >
              Đăng nhập
            </a>
          )}
        </div>
      </div>
    </header>
  );
};

export default Navbar;
