import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext'; // Import useTheme
import { FaSun, FaMoon, FaLeaf } from 'react-icons/fa'; // Import icons

const Navbar = () => {
  const { user, logout } = useAuth();
  const { setTheme } = useTheme(); // Lấy hàm setTheme

  return (
    <nav className="main-nav container">
      <Link to="/" className="logo">
        <h1>CarePlus</h1>
      </Link>
      <div className="nav-links">
        <Link to="/">Trang chủ</Link>
        <Link to="/about">Về chúng tôi</Link>
        <Link to="/contact">Liên hệ</Link>
      </div>
      <div className="nav-right-section">
        <div className="theme-switcher">
          <button onClick={() => setTheme('theme-light')} title="Light Mode"><FaSun /></button>
          <button onClick={() => setTheme('theme-dark')} title="Dark Mode"><FaMoon /></button>
          <button onClick={() => setTheme('theme-green')} title="Green Mode"><FaLeaf /></button>
        </div>
        <div className="auth-section">
          {user ? (
            <>
              <Link to="/create-post">Đăng bài</Link>
              <span>Chào, {user.name}!</span>
              <button onClick={logout} className="logout-button">Đăng xuất</button>
            </>
          ) : (
            <a href="http://localhost:3001/api/auth/google" className="login-button">
              Đăng nhập
            </a>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
