// src/pages/HomePage.js
import React from 'react';
import data from '../data.json';
import ArticleCard from '../components/ArticleCard';
import { useAuth } from '../context/AuthContext';

const HomePage = ({ selectedCategory, searchTerm }) => {
  const { user } = useAuth();

  const filteredArticles = data.articles
    .filter(article => selectedCategory === 'all' || article.category === selectedCategory)
    .filter(article => 
      !searchTerm ||
      article.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      article.excerpt.toLowerCase().includes(searchTerm.toLowerCase())
    );

  return (
    <div className="space-y-8">
      {user && (
        <div className="card-bg p-6 rounded-lg">
          <h2 className="text-2xl font-bold text-main">Chào mừng trở lại, {user.name}!</h2>
        </div>
      )}
      
      <section>
        <h2 className="text-3xl font-bold mb-6 pb-3 border-b-2 border-accent inline-block text-main">
          {searchTerm ? `Kết quả cho "${searchTerm}"` : 'Bài viết mới nhất'}
        </h2>
        
        {filteredArticles.length > 0 ? (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-8">
            {filteredArticles.map(article => (
              <ArticleCard key={article.id} article={article} />
            ))}
          </div>
        ) : (
          <div className="card-bg p-8 rounded-lg text-center">
            <p className="text-subtle">Không tìm thấy bài viết nào phù hợp.</p>
          </div>
        )}
      </section>
    </div>
  );
};

export default HomePage;
