// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { FaSearch } from 'react-icons/fa';

const DEFAULT_AVATAR = 'https://gamek.mediacdn.vn/133514250583805952/2023/1/14/vi-daochich-dtcl-6-16736364925801623067077-29-0-657-1200-crop-16736691436801405945754.png';

const Navbar = ({ setSearchTerm }) => {
  const { user, logout } = useAuth();
  const [localSearch, setLocalSearch] = useState('');

  const handleSearch = (e) => {
    e.preventDefault();
    setSearchTerm(localSearch);
  };

  return (
    <header className="sticky top-0 z-50 shadow-lg bg-[#1f2937] text-white">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center py-3">
          {/* Logo & Tên trang */}
          <Link to="/" className="flex items-center gap-3">
            <img src="https://ttagencyads.com/wp-content/uploads/2022/03/Logo-rong-do.jpg" alt="CarePlus Logo" className="w-10 h-10 rounded-full object-cover"/>
            <div><h1 className="text-xl font-bold text-white">CarePlus</h1></div>
          </Link>
          
          {/* Nav Links */}
          <nav className="hidden md:flex items-center gap-6 font-semibold">
            <Link to="/" className="hover:text-gray-300">Trang chủ</Link>
            <Link to="/about" className="hover:text-gray-300">Về chúng tôi</Link>
            <Link to="/contact" className="hover:text-gray-300">Liên hệ</Link>
          </nav>

          {/* Actions */}
          <div className="flex items-center gap-4">
            {user ? (
              <div className="flex items-center gap-3">
                <Link to="/profile" className="flex items-center gap-3">
                  <span className="font-semibold hidden sm:inline">{user.name}</span>
                  <img src={user.avatar || DEFAULT_AVATAR} alt="User Avatar" className="w-9 h-9 rounded-full border-2 border-blue-400 object-cover"/>
                </Link>
                <button onClick={logout} className="bg-red-500 text-white px-3 py-1.5 rounded-md font-medium hover:bg-red-600 text-sm">Thoát</button>
              </div>
            ) : (
              <Link to="/login" className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-md font-bold transition-colors">
                Đăng nhập
              </Link>
            )}
          </div>
        </div>
        
        {/* THANH TÌM KIẾM */}
        <div className="pb-4">
            <form onSubmit={handleSearch} className="relative">
              <input 
                  type="text" 
                  value={localSearch}
                  onChange={(e) => setLocalSearch(e.target.value)}
                  placeholder="Tìm kiếm bài viết..." 
                  className="w-full py-2 pl-4 pr-12 rounded-full border-2 border-transparent bg-white text-gray-900 placeholder:text-gray-500 focus:outline-none focus:ring-2 focus:ring-blue-500"
              />
              <button type="submit" className="absolute right-2 top-1/2 -translate-y-1/2 p-2 rounded-full text-gray-600 hover:text-blue-600">
                  <FaSearch />
              </button>
            </form>
        </div>
      </div>
    </header>
  );
};

export default Navbar;
