// src/components/ArticleCard.js
import React from 'react';
import { Link } from 'react-router-dom';

const ArticleCard = ({ article }) => {
  const { id, title, excerpt, imageUrl, author, date } = article;

  return (
    // Link đến trang chi tiết
    <Link to={`/article/${id}`} className="block bg-white dark:bg-gray-800 rounded-lg shadow-lg overflow-hidden transform hover:-translate-y-2 transition-transform duration-300">
        <img className="w-full h-48 object-cover" src={imageUrl} alt={title} />
        <div className="p-5">
          <h3 className="font-bold text-lg mb-2 leading-tight">{title}</h3>
          <div className="text-xs text-gray-500 dark:text-gray-400 mb-3">
            <span>{author} | {date}</span>
          </div>
          <p className="text-sm mb-4">{excerpt}</p>
          <span className="font-semibold text-blue-600 dark:text-blue-400">Xem chi tiết →</span>
        </div>
    </Link>
  );
};

export default ArticleCard;
