// src/pages/ChatHistoryPage.js
import React, { useState } from 'react';
import api from '../services/api';

const ChatHistoryPage = () => {
    const [history, setHistory] = useState([]);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        api.get('/chatbot/history')
            .then(res => setHistory(res.data))
            .catch(err => console.error("Lỗi khi tải lịch sử chat:", err))
            .finally(() => setIsLoading(false));
    }, []);

    if (isLoading) return <p className="text-center p-10">Đang tải lịch sử...</p>;

    return (
        <div className="card-bg p-8 rounded-lg">
            <h1 className="text-3xl font-bold mb-6 text-main border-b border-main pb-4">Lịch sử Tư vấn</h1>
            {history.length > 0 ? (
                <div className="space-y-4">
                    {history.map((chat, index) => (
                        <div key={index} className="p-4 border-b border-main last:border-b-0">
                            <p><strong className="text-accent">Bạn:</strong> <span className="text-main">{chat.message}</span></p>
                            <p className="mt-2"><strong className="text-main">Trợ lý:</strong> <span className="text-subtle">{chat.response}</span></p>
                            <p className="text-xs text-subtle text-right mt-2">{new Date(chat.created_at).toLocaleString('vi-VN')}</p>
                        </div>
                    ))}
                </div>
            ) : (
                <p className="text-subtle text-center py-8">Bạn chưa có lịch sử tư vấn nào.</p>
            )}
        </div>
    );
};

export default ChatHistoryPage;
