// src/pages/ProfilePage.js
import React from 'react';
import { useAuth } from '../context/AuthContext';
import { Navigate } from 'react-router-dom';

const ProfilePage = () => {
  const { user, logout } = useAuth(); // Lấy user từ AuthContext

  if (!user) {
    return <Navigate to="/" />;
  }

  // 'user.avatar', 'user.name', 'user.email' đều được cung cấp
  // từ backend sau khi bạn đăng nhập Google.
  return (
    <div className="bg-white dark:bg-gray-800 shadow-lg p-6 md:p-10 rounded-lg max-w-2xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center text-gray-800 dark:text-white">Trang cá nhân</h1>
      
      <div className="flex flex-col items-center gap-4">
        <img 
          src={user.avatar} 
          alt="User Avatar" 
          className="w-32 h-32 rounded-full border-4 border-blue-500 shadow-md"
        />
        <div className="text-center">
          <h2 className="text-2xl font-semibold text-gray-700 dark:text-gray-200">{user.name}</h2>
          <p className="text-gray-500 dark:text-gray-400">{user.email}</p>
        </div>
        
        <button 
          onClick={logout}
          className="mt-6 bg-red-500 text-white px-6 py-2 rounded-md font-medium hover:bg-red-600 transition-colors"
        >
          Đăng xuất
        </button>
      </div>
    </div>
  );
};

export default ProfilePage;
