// src/pages/HomePage.js
import React from 'react';
import data from '../data.json';
import ArticleCard from '../components/ArticleCard';
import { FaSearch } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';

const HomePage = ({ selectedCategory }) => {
    const { user } = useAuth();
    
    const filteredArticles = selectedCategory === 'all'
        ? data.articles
        : data.articles.filter(article => article.category === selectedCategory);

    return (
        <div className="space-y-8">
            {user && (
              <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md">
                <h2 className="text-2xl font-bold text-gray-800 dark:text-white">
                  Chào mừng trở lại, {user.name}!
                </h2>
                <p className="text-gray-600 dark:text-gray-300 mt-2">
                  Hãy cùng khám phá những thông tin y khoa mới nhất nhé.
                </p>
              </div>
            )}

            <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md">
                <div className="relative">
                    <input type="text" placeholder="Tìm kiếm bài viết, y tế, triệu chứng..." className="w-full py-3 pl-4 pr-16 text-gray-700 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white dark:border-gray-600"/>
                    <button className="absolute right-2 top-1/2 -translate-y-1/2 bg-blue-600 text-white p-2.5 rounded-full hover:bg-blue-700 transition-colors">
                        <FaSearch />
                    </button>
                </div>
            </div>

            <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-md">
                <h2 className="text-2xl font-bold text-gray-800 dark:text-white mb-6 border-b border-gray-300 dark:border-gray-700 pb-3">
                    {selectedCategory === 'all' ? 'Bài viết mới nhất' : `Bài viết về: ${selectedCategory}`}
                </h2>
                {filteredArticles.length > 0 ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-2 gap-6">
                        {filteredArticles.map(article => (
                            <ArticleCard key={article.id} {...article} />
                        ))}
                    </div>
                ) : (
                    <p className="text-center text-gray-500 dark:text-gray-400 mt-8">Không tìm thấy bài viết nào trong danh mục này.</p>
                )}
            </div>
        </div>
    );
};

export default HomePage;
