// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
import { FaSun, FaMoon, FaSearch } from 'react-icons/fa';

const Navbar = ({ setSearchTerm }) => {
  const { user, logout } = useAuth();
  const { themeName, toggleTheme } = useTheme();
  const [localSearch, setLocalSearch] = useState('');
  const handleSearch = (e) => { e.preventDefault(); setSearchTerm(localSearch); };

  return (
    <header className="card-bg border-b border-main sticky top-0 z-50">
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center py-3">
          {/* ... (Logo, Nav Links, User Actions) ... */}
        </div>
        <div className="pb-4">
          <form onSubmit={handleSearch} className="relative">
            <input 
              type="text" 
              value={localSearch}
              onChange={(e) => setLocalSearch(e.target.value)}
              placeholder="Tìm kiếm bài viết..." 
              className="w-full py-2 pl-4 pr-12 rounded-full border border-main bg-primary text-main placeholder:text-subtle focus:outline-none focus:ring-2 focus:ring-accent"
              style={{backgroundColor: 'var(--bg-primary)'}} // Đảm bảo nền input luôn đúng
            />
            <button type="submit" className="absolute right-2 top-1/2 -translate-y-1/2 p-2 rounded-full text-subtle text-accent-hover">
              <FaSearch />
            </button>
          </form>
        </div>
      </div>
    </header>
  );
};
export default Navbar;
