// src/App.js
import React from 'react';
import { Routes, Route } from 'react-router-dom';
import { useTheme } from './context/ThemeContext';
import HomePage from './pages/HomePage';
import LoginPage from './pages/LoginPage';
import CreatePostPage from './pages/CreatePostPage';
import ProtectedRoute from './components/ProtectedRoute';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import AboutPage from './pages/AboutPage'; // <-- Import trang mới
import ContactPage from './pages/ContactPage'; // <-- Import trang mới

function App() {
  const { theme } = useTheme();

  return (
    <div className={`app-container ${theme} bg-gray-100 dark:bg-gray-900 min-h-screen transition-colors duration-300`}>
      <Navbar />
      <div className="container mx-auto flex flex-col md:flex-row gap-8 px-4 py-8">
        <Sidebar />
        <main className="flex-grow w-full md:w-auto">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/about" element={<AboutPage />} /> {/* <-- Thêm Route Về chúng tôi */}
            <Route path="/contact" element={<ContactPage />} /> {/* <-- Thêm Route Liên hệ */}
            <Route path="/login" element={<LoginPage />} />
            <Route
              path="/create-post"
              element={
                <ProtectedRoute>
                  <CreatePostPage />
                </ProtectedRoute>
              }
            />
          </Routes>
        </main>
      </div>
    </div>
  );
}

export default App;
