// src/pages/HomePage.js

import React, { useState, useEffect } from 'react';
import { getArticles } from '../services/api';
import ArticleCard from '../components/ArticleCard'; // Import component mới

const HomePage = () => {
    const [articles, setArticles] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchArticles = async () => {
            try {
                const response = await getArticles();
                setArticles(response.data);
            } catch (error) {
                console.error("Lỗi khi tải bài viết:", error);
            } finally {
                setLoading(false);
            }
        };
        fetchArticles();
    }, []);

    if (loading) {
        return <div className="text-center py-20 text-xl font-semibold">Đang tải bài viết...</div>;
    }

    return (
        <div className="bg-gray-50 min-h-screen">
            <main className="container mx-auto px-4 py-10">
                <div className="text-center mb-12">
                    <h1 className="text-4xl font-bold text-gray-800">Kiến thức Y khoa</h1>
                    <p className="text-lg text-gray-600 mt-2">Cập nhật những thông tin sức khỏe mới nhất và hữu ích.</p>
                </div>

                {articles.length > 0 ? (
                    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
                        {articles.map(article => (
                            <ArticleCard
                                key={article.id}
                                id={article.id}
                                title={article.title}
                                excerpt={article.content.substring(0, 120) + '...'}
                                // imageUrl={article.imageUrl} // Sẽ thêm sau nếu DB có
                            />
                        ))}
                    </div>
                ) : (
                    <p className="text-center text-gray-500">Chưa có bài viết nào.</p>
                )}
            </main>
        </div>
    );
};

export default HomePage;
