import React, { createContext, useState, useEffect, useContext } from 'react';
import api from '../services/api'; // Dùng axios instance

const AuthContext = createContext(null);

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true); // Trạng thái loading để tránh chớp giao diện

  useEffect(() => {
    // Khi app vừa load, gọi API để kiểm tra xem đã đăng nhập chưa
    const checkLoggedIn = async () => {
      try {
        const response = await api.get('/auth/user');
        setUser(response.data.user);
      } catch (error) {
        console.error("Not authenticated", error);
        setUser(null);
      } finally {
        setLoading(false); // Dừng loading sau khi đã có kết quả
      }
    };
    checkLoggedIn();
  }, []);

  const logout = async () => {
    try {
      await api.get('/auth/logout');
      setUser(null);
      window.location.href = '/'; // Về trang chủ sau khi logout
    } catch (error) {
      console.error("Logout failed", error);
    }
  };

  const value = {
    user,
    setUser,
    loading,
    logout,
  };

  // Chỉ render children khi không còn loading
  return (
    <AuthContext.Provider value={value}>
      {!loading && children}
    </AuthContext.Provider>
  );
};
