// src/App.js
import React from 'react';
import { Routes, Route } from 'react-router-dom';
// ... các import khác ...
import ProfilePage from './pages/ProfilePage'; // <-- Import trang mới

function App() {
  const { theme } = useTheme();

  return (
    <div className={`app-container ${theme} ...`}>
      <Navbar />
      <div className="container mx-auto flex flex-col md:flex-row gap-8 px-4 py-8">
        <Sidebar />
        <main className="flex-grow w-full md:w-auto">
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/contact" element={<ContactPage />} />
            <Route path="/login" element={<LoginPage />} />

            {/* THÊM ROUTE MỚI CHO TRANG CÁ NHÂN */}
            <Route 
              path="/profile" 
              element={
                <ProtectedRoute> {/* Bọc trong ProtectedRoute để chỉ người đã đăng nhập mới vào được */}
                  <ProfilePage />
                </ProtectedRoute>
              } 
            />

            <Route
              path="/create-post"
              element={
                <ProtectedRoute>
                  <CreatePostPage />
                </ProtectedRoute>
              }
            />
          </Routes>
        </main>
      </div>
    </div>
  );
}

export default App;
