// src/pages/RegisterPage.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../services/api';

const RegisterPage = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await api.post('/auth/register', { name, email, password });
      window.location.href = '/'; // Tải lại trang để AuthContext cập nhật
    } catch (err) {
      setError(err.response?.data?.message || 'Đã có lỗi xảy ra.');
    }
  };

  return (
    <div className="card-bg p-8 rounded-lg max-w-md mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-center text-main">Tạo tài khoản</h1>
      {error && <p className="bg-red-100 text-red-700 p-3 rounded mb-4">{error}</p>}
      <form onSubmit={handleSubmit} className="space-y-4">
        {/* ... form fields ... */}
        <button type="submit" className="w-full bg-accent bg-accent-hover text-white font-bold py-3 rounded-lg">Đăng ký</button>
      </form>
      <p className="mt-6 text-center text-subtle">
        Đã có tài khoản? <Link to="/login" className="font-semibold text-accent hover:underline">Đăng nhập</Link>
      </p>
    </div>
  );
};
export default RegisterPage;
