// src/components/Sidebar.js
import React, { useState } from 'react';
import { medicalCategories } from '../categories';
import { Link } from 'react-router-dom';
import { FaChevronDown } from 'react-icons/fa';

const CollapsibleCategory = ({ title, subitems, setSelectedCategory }) => {
  const [isOpen, setIsOpen] = useState(false); // Mặc định đóng

  return (
    <li className="mb-4">
      <button onClick={() => setIsOpen(!isOpen)} className="w-full flex justify-between items-center font-semibold">
        <span>{title}</span>
        <FaChevronDown className={`transform transition-transform duration-300 ${isOpen ? 'rotate-180' : ''}`} />
      </button>
      <div className={`overflow-hidden transition-all duration-300 ease-in-out ${isOpen ? 'max-h-screen mt-2' : 'max-h-0'}`}>
        <ul className="ml-4 space-y-2 border-l pl-3">
          {subitems.map((item, subIndex) => (
            <li key={subIndex}>
              <Link to="/" onClick={() => setSelectedCategory(item.filterKey)} className="block hover:underline">
                {item.name}
              </Link>
            </li>
          ))}
        </ul>
      </div>
    </li>
  );
};

const Sidebar = ({ setSelectedCategory }) => {
  return (
    // Sử dụng class card-bg để có nền mờ
    <aside className="card-bg w-full md:w-64 lg:w-72 flex-shrink-0 p-6 rounded-lg self-start sticky top-32">
      <h2 className="text-xl font-bold mb-4 pb-2 border-b border-main text-main">
        Danh mục Y tế
      </h2>
      <nav>
        <ul>
          <li className="mb-4">
            <button onClick={() => setSelectedCategory('all')} className="w-full text-left font-semibold text-accent text-accent-hover">
              Tất cả bài viết
            </button>
          </li>
          {medicalCategories.map((category, index) => (
            <CollapsibleCategory key={index} {...category} setSelectedCategory={setSelectedCategory} />
          ))}
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
