// src/pages/HomePage.js
import React from 'react';
import data from '../data.json';
import ArticleCard from '../components/ArticleCard';
import { FaSearch } from 'react-icons/fa';

const HomePage = () => {
    const articles = data.articles;

    return (
        <div className="space-y-8">
            {/* Thanh tìm kiếm */}
            <div className="bg-white p-6 rounded-lg shadow-md">
                <div className="relative">
                    <input
                        type="text"
                        placeholder="Tìm kiếm bài viết, y tế, triệu chứng..."
                        className="w-full py-3 pl-4 pr-16 text-gray-700 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                    <button className="absolute right-2 top-1/2 -translate-y-1/2 bg-blue-600 text-white p-2.5 rounded-full hover:bg-blue-700 transition-colors">
                        <FaSearch />
                    </button>
                </div>
            </div>

            {/* Danh sách bài viết */}
            <div className="bg-white p-6 rounded-lg shadow-md">
                <h2 className="text-2xl font-bold text-gray-800 mb-6 border-b pb-3">Bài viết mới nhất</h2>
                {articles.length > 0 ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-3 gap-6">
                        {articles.map(article => (
                            <ArticleCard
                                key={article.id}
                                id={article.id}
                                title={article.title}
                                excerpt={article.excerpt}
                                imageUrl={article.imageUrl}
                                author={article.author}
                                date={article.date}
                            />
                        ))}
                    </div>
                ) : (
                    <p>Chưa có bài viết nào.</p>
                )}
            </div>
        </div>
    );
};

export default HomePage;
