// src/components/ArticleCard.js
import React from 'react';
import { Link } from 'react-router-dom';

const ArticleCard = ({ id, title, excerpt, imageUrl, author, date }) => {
  return (
    // Sử dụng class của Tailwind để tạo hiệu ứng
    <div className="bg-white rounded-lg shadow-lg overflow-hidden transform hover:-translate-y-2 transition-transform duration-300 ease-in-out border border-gray-200">
      <Link to={`/article/${id}`}>
        <img
          className="w-full h-48 object-cover"
          src={imageUrl || 'https://via.placeholder.com/400x250?text=Health+Image'}
          alt={title}
        />
        <div className="p-5">
          <h3 className="font-bold text-lg mb-2 text-gray-800 hover:text-blue-600 leading-tight">{title}</h3>
          <div className="text-xs text-gray-500 mb-3">
            <span>Tác giả: {author} | Ngày đăng: {date}</span>
          </div>
          <p className="text-gray-600 text-sm mb-4">{excerpt}</p>
          <span className="font-semibold text-blue-600 hover:underline">
            Xem trên nguồn →
          </span>
        </div>
      </Link>
    </div>
  );
};

export default ArticleCard;
