// src/components/CommentSection.js
import React, { useState, useEffect } from 'react';
import api from '../services/api';
import { useAuth } from '../context/AuthContext';
import { Link } from 'react-router-dom';

const CommentSection = ({ articleId }) => {
    const [comments, setComments] = useState([]);
    const [newComment, setNewComment] = useState('');
    const [isLoading, setIsLoading] = useState(true);
    const { user } = useAuth();

    // Lấy tất cả bình luận khi component được tải
    useEffect(() => {
        const fetchComments = async () => {
            try {
                const res = await api.get(`/comments/${articleId}`);
                setComments(res.data);
            } catch (error) {
                console.error("Lỗi khi tải bình luận:", error);
            } finally {
                setIsLoading(false);
            }
        };
        fetchComments();
    }, [articleId]);

    // Xử lý khi người dùng gửi bình luận
    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!newComment.trim()) return; // Không gửi nếu bình luận trống

        try {
            // Gửi bình luận lên server
            const res = await api.post(`/comments/${articleId}`, { comment: newComment });
            
            // Cập nhật state để hiển thị ngay bình luận mới ở trên cùng
            setComments([res.data, ...comments]); 
            setNewComment(''); // Xóa nội dung trong ô nhập
        } catch (error) {
            console.error("Lỗi khi gửi bình luận:", error);
            alert('Đã xảy ra lỗi. Vui lòng đăng nhập và thử lại.');
        }
    };

    if (isLoading) {
        return <p className="text-subtle">Đang tải bình luận...</p>;
    }

    return (
        <div className="mt-12 pt-8 border-t border-main">
            <h2 className="text-2xl font-bold mb-6 text-main">Bình luận ({comments.length})</h2>
            
            {/* Form nhập bình luận */}
            {user ? (
                <form onSubmit={handleSubmit} className="mb-8">
                    <textarea 
                        value={newComment} 
                        onChange={e => setNewComment(e.target.value)} 
                        placeholder="Viết bình luận của bạn..." 
                        rows="4" 
                        className="w-full p-3 rounded-md border border-main bg-transparent text-main focus:outline-none focus:ring-2 focus:ring-accent"
                    />
                    <button 
                        type="submit" 
                        className="mt-2 bg-accent bg-accent-hover text-white font-bold py-2 px-5 rounded-lg transition-colors"
                    >
                        Gửi bình luận
                    </button>
                </form>
            ) : (
                <p className="text-subtle mb-8 p-4 border border-main rounded-lg">
                    Vui lòng <Link to="/login" className="font-semibold text-accent underline">đăng nhập</Link> để tham gia bình luận.
                </p>
            )}

            {/* Danh sách bình luận đã có */}
            <div className="space-y-6">
                {comments.length > 0 ? comments.map(comment => (
                    <div key={comment.id} className="flex gap-4">
                        <img src={comment.avatar} alt={comment.name} className="w-10 h-10 rounded-full flex-shrink-0"/>
                        <div className="card-bg p-4 rounded-lg flex-grow">
                            <div className="flex items-center gap-2">
                                <p className="font-semibold text-main">{comment.name}</p>
                                <p className="text-xs text-subtle">·</p>
                                <p className="text-xs text-subtle">{new Date(comment.created_at).toLocaleString('vi-VN')}</p>
                            </div>
                            <p className="mt-2 text-main whitespace-pre-wrap">{comment.comment}</p>
                        </div>
                    </div>
                )) : (
                    <p className="text-subtle">Chưa có bình luận nào. Hãy là người đầu tiên!</p>
                )}
            </div>
        </div>
    );
};

export default CommentSection;
