// src/pages/HomePage.js
import React from 'react';
import data from '../data.json';
import ArticleCard from '../components/ArticleCard';
import { FaSearch } from 'react-icons/fa';

const HomePage = ({ selectedCategory, searchTerm }) => {
    // Lọc bài viết dựa trên danh mục (từ Sidebar) và từ khóa tìm kiếm (từ App.js)
    const filteredArticles = data.articles
        .filter(article => 
            selectedCategory === 'all' || article.category === selectedCategory
        )
        .filter(article => 
            !searchTerm || // Nếu không có từ khóa tìm kiếm thì bỏ qua
            article.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
            article.excerpt.toLowerCase().includes(searchTerm.toLowerCase())
        );

    return (
        <div className="space-y-8">
            {/* THANH TÌM KIẾM NẰM Ở ĐÂY.
              Nó không nằm trong Navbar mà là component đầu tiên của Trang chủ.
            */}
            <div className="p-6 rounded-lg shadow-md">
                <div className="relative">
                    <input 
                        type="text" 
                        placeholder="Tìm kiếm bài viết, y tế, triệu chứng..." 
                        className="w-full py-3 pl-4 pr-16 rounded-full"
                        // Logic tìm kiếm sẽ được xử lý ở file App.js
                    />
                    <button className="absolute right-2 top-1/2 -translate-y-1/2 bg-blue-600 text-white p-2.5 rounded-full hover:bg-blue-700">
                        <FaSearch />
                    </button>
                </div>
            </div>

            {/* Phần hiển thị các bài viết */}
            <div className="p-6 rounded-lg shadow-md">
                <h2 className="text-2xl font-bold mb-6 border-b pb-3">
                    Bài viết mới nhất
                </h2>
                {filteredArticles.length > 0 ? (
                    <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-2 gap-6">
                        {filteredArticles.map(article => (
                            <ArticleCard key={article.id} article={article} />
                        ))}
                    </div>
                ) : (
                    <p className="text-center mt-8">
                        Không tìm thấy bài viết nào phù hợp.
                    </p>
                )}
            </div>
        </div>
    );
};

export default HomePage;
