// src/components/ChatbotWidget.js
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../context/AuthContext';
import { FaComments, FaTimes } from 'react-icons/fa';
import api from '../services/api';

const BOT_AVATAR = 'https://maunailxinh.com/wp-content/uploads/2025/06/avatar-bua-troll-6.jpg';

const ChatbotWidget = () => {
  const { user } = useAuth();
  const [isOpen, setIsOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [options, setOptions] = useState([]);
  const [step, setStep] = useState('greeting');
  const [selectedTopicId, setSelectedTopicId] = useState(null);
  const chatEndRef = useRef(null);

  useEffect(() => {
    if (isOpen && messages.length === 0) {
      const initialMessage = `Chào ${user ? user.name.split(' ')[0] : 'bạn'}! Tôi có thể giúp gì cho bạn?`;
      setMessages([{ type: 'bot', text: initialMessage }]);
      api.get('/chatbot/topics').then(res => {
        setOptions(res.data.map(topic => ({ id: topic.id, text: topic.id.replace(/-/g, ' ') })));
      });
    }
  }, [isOpen, user, messages.length]);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages, options]);

  const handleOptionClick = (option) => {
    setMessages(prev => [...prev, { type: 'user', text: option.text }]);
    setOptions([]);

    if (step === 'greeting') {
      setSelectedTopicId(option.id);
      // Giả lập API call để lấy options. Trong thực tế bạn sẽ gọi API
      api.get(`/chatbot/topics`).then(res => { // Lấy lại topics để tìm đúng
        const fullTopic = res.data.find(t => t.id === option.id);
        if (fullTopic) {
           setTimeout(() => {
                // Bạn cần đảm bảo API trả về 'question' và 'options'
                // Tạm thời giả lập ở đây
                const topicData = { question: "Vui lòng chọn vấn đề cụ thể:", options: { opt1: 'Lựa chọn A', opt2: 'Lựa chọn B'} };
                setMessages(prev => [...prev, { type: 'bot', text: topicData.question }]);
                setOptions(Object.entries(topicData.options).map(([key, value]) => ({ id: key, text: value })));
                setStep('options');
            }, 500);
        }
      });
    } else if (step === 'options') {
      api.post('/chatbot/answer', { topicId: selectedTopicId, optionId: option.id }).then(res => {
        const escalation = "Nếu thông tin này chưa đủ, vui lòng đợi trong giây lát, chúng tôi sẽ liên kết với nhân viên tư vấn để hỗ trợ cụ thể hơn.";
        setTimeout(() => {
          setMessages(prev => [...prev, { type: 'bot', text: res.data.answer }, { type: 'bot', text: escalation }]);
          setStep('answer');
        }, 1000);
      });
    }
  };

  const resetChat = () => {
    setMessages([{ type: 'bot', text: 'Bạn cần hỗ trợ gì khác không?' }]);
    api.get('/chatbot/topics').then(res => {
      setOptions(res.data.map(topic => ({ id: topic.id, text: topic.id.replace(/-/g, ' ') })));
    });
    setStep('greeting');
  };

  return (
    <>
      <button onClick={() => setIsOpen(!isOpen)} className="fixed bottom-24 right-5 bg-accent w-16 h-16 rounded-full text-white flex items-center justify-center shadow-lg transform transition-transform hover:scale-110 z-50">
        {isOpen ? <FaTimes size={24} /> : <FaComments size={24} />}
      </button>
      {isOpen && (
        <div className="fixed bottom-5 right-5 w-[calc(100%-2.5rem)] max-w-sm h-[70vh] max-h-[500px] card-bg rounded-lg shadow-xl flex flex-col z-50">
          <div className="p-4 border-b border-main flex items-center gap-3">
            <img src={BOT_AVATAR} alt="bot avatar" className="w-10 h-10 rounded-full" />
            <h3 className="font-bold text-main">Trợ lý sức khỏe CarePlus</h3>
          </div>
          <div className="flex-grow p-4 overflow-y-auto space-y-4">
            {messages.map((msg, index) => (
              <div key={index} className={`flex items-start gap-3 ${msg.type === 'user' ? 'flex-row-reverse' : ''}`}>
                {msg.type === 'bot' && <img src={BOT_AVATAR} alt="bot" className="w-8 h-8 rounded-full flex-shrink-0"/>}
                <div className={`p-3 rounded-lg max-w-[80%] ${msg.type === 'user' ? 'bg-accent text-white' : 'bg-gray-200 dark:bg-gray-700 text-main'}`}>
                  {msg.text}
                </div>
              </div>
            ))}
            {options.length > 0 && (
                <div className="flex items-start gap-3">
                    <img src={BOT_AVATAR} alt="bot" className="w-8 h-8 rounded-full flex-shrink-0"/>
                    <div className="p-3 rounded-lg bg-gray-200 dark:bg-gray-700 space-y-2 w-full">
                        {options.map(opt => (
                            <button key={opt.id} onClick={() => handleOptionClick(opt)} className="block w-full text-left p-2 rounded-md border border-accent text-accent hover:bg-accent hover:text-white transition-colors">
                                {opt.text}
                            </button>
                        ))}
                    </div>
                </div>
            )}
            <div ref={chatEndRef} />
          </div>
          {step === 'answer' && (
            <div className="p-4 border-t border-main">
                <button onClick={resetChat} className="w-full p-2 rounded-md border border-accent text-accent hover:bg-accent hover:text-white transition-colors">Hỏi về vấn đề khác</button>
            </div>
          )}
        </div>
      )}
    </>
  );
};
export default ChatbotWidget;
