// src/App.js
import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import { useTheme } from './context/ThemeContext';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import ProtectedRoute from './components/ProtectedRoute';
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import ContactPage from './pages/ContactPage';
import LoginPage from './pages/LoginPage';
import ArticleDetailPage from './pages/ArticleDetailPage'; // <-- Import trang chi tiết
import ProfilePage from './pages/ProfilePage';

function App() {
  const { themeName } = useTheme();
  const [selectedCategory, setSelectedCategory] = useState('all');

  return (
    // Áp dụng class theme trực tiếp
    <div className={themeName === 'dark' ? 'theme-dark' : ''}>
      <Navbar />
      <div className="container mx-auto flex flex-col md:flex-row gap-8 px-4 py-8">
        <Sidebar setSelectedCategory={setSelectedCategory} />
        <main className="flex-grow w-full md:w-auto">
          {/* ... (Routes giữ nguyên) ... */}
        </main>
      </div>
    </div>
  );
}

export default App;
