// src/components/Sidebar.js
import React from 'react';
import { medicalCategories } from '../categories'; // Import dữ liệu
import { Link } from 'react-router-dom';

const Sidebar = () => {
  return (
    <aside className="w-full md:w-64 lg:w-72 flex-shrink-0 bg-white shadow-md p-6 rounded-lg">
      <h2 className="text-xl font-bold mb-4 text-gray-800 border-b pb-2">Danh mục Y tế</h2>
      <nav>
        <ul>
          {medicalCategories.map((category, index) => (
            <li key={index} className="mb-4">
              <h3 className="font-semibold text-gray-700">{category.title}</h3>
              <ul className="mt-2 ml-4 space-y-1">
                {category.subitems.map((item, subIndex) => (
                  <li key={subIndex}>
                    <Link to={`/category/${item.toLowerCase().replace(/ /g, '-')}`}
                       className="text-gray-600 hover:text-blue-600 transition-colors duration-200">
                      {item}
                    </Link>
                  </li>
                ))}
              </ul>
            </li>
          ))}
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
