// src/components/Navbar.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext'; // Import từ context mới
import { FaSun, FaMoon, FaLeaf } from 'react-icons/fa';

const Navbar = () => {
  const { user, logout } = useAuth();
  // Lấy đúng tên biến từ context mới
  const { themeName, toggleTheme } = useTheme(); 

  return (
    <header className="bg-white dark:bg-gray-800 shadow-md sticky top-0 z-50 transition-colors duration-300">
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        {/* ... (Phần Logo) ... */}
        <Link to="/" className="flex items-center gap-3">
          <img src="https://ttagencyads.com/wp-content/uploads/2022/03/Logo-rong-do.jpg" alt="CarePlus Logo" className="w-10 h-10 rounded-full object-cover"/>
          <div><h1 className="text-xl font-bold text-blue-600 dark:text-blue-400">CarePlus</h1></div>
        </Link>
        
        {/* ... (Phần Nav links) ... */}
        <nav className="hidden md:flex items-center gap-6">
          <Link to="/" className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600">Trang chủ</Link>
          <Link to="/about" className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600">Về chúng tôi</Link>
          <Link to="/contact" className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600">Liên hệ</Link>
        </nav>

        {/* ... (Phần Actions) ... */}
        <div className="flex items-center gap-4">
          {/* Nút đổi Theme - ĐÃ CẬP NHẬT LOGIC */}
          <button 
            onClick={toggleTheme} // Gọi hàm toggleTheme
            className="p-2 rounded-full text-gray-700 dark:text-gray-200 hover:bg-gray-200 dark:hover:bg-gray-700" 
            title="Đổi giao diện"
          >
            {/* Hiển thị icon dựa trên themeName */}
            {themeName === 'light' && <FaMoon size={18} />}
            {themeName === 'dark' && <FaLeaf size={18} />}
            {themeName === 'green' && <FaSun size={18} />}
          </button>

          {/* ... (Phần Đăng nhập / User) ... */}
          {user ? (
            <div className="flex items-center gap-3">
              <Link to="/profile" className="flex items-center gap-3 hover:opacity-80 transition-opacity">
                <span className="font-semibold text-gray-700 dark:text-gray-200 hidden sm:inline">{user.name}</span>
                <img src={user.avatar} alt="User Avatar" className="w-9 h-9 rounded-full border-2 border-blue-500"/>
              </Link>
              <button onClick={logout} className="bg-red-500 text-white px-3 py-1.5 rounded-md font-medium hover:bg-red-600 text-sm">Thoát</button>
            </div>
          ) : (
            <a href="http://localhost:3001/api/auth/google" className="bg-blue-600 text-white px-4 py-2 rounded-md font-medium hover:bg-blue-700">Đăng nhập</a>
          )}
        </div>
      </div>
    </header>
  );
};

export default Navbar;
