// src/pages/ProfilePage.js
import React, { useState, useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { Navigate, Link } from 'react-router-dom';
import api from '../services/api';

// --- Component con để hiển thị nội dung cho tab "Bài viết của tôi" (Code cũ giữ nguyên) ---
const MyPosts = () => (
  <div>
    <h3 className="text-xl font-semibold mb-4 text-main">Lịch sử bài đăng</h3>
    <div className="border-t border-main pt-4">
      <p className="text-subtle">Chưa có bài đăng nào.</p>
    </div>
  </div>
);

// --- Component con để hiển thị nội dung cho tab "Bình luận của tôi" (Code cũ giữ nguyên) ---
const MyComments = () => (
  <div>
    <h3 className="text-xl font-semibold mb-4 text-main">Lịch sử bình luận</h3>
    <div className="border-t border-main pt-4">
      <p className="text-subtle">Chưa có bình luận nào.</p>
    </div>
  </div>
);

// --- Component chính của trang cá nhân (Code mới đã hoàn thiện) ---
const ProfilePage = () => {
  const { user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('posts');
  const [postCount, setPostCount] = useState(0);
  const [commentCount, setCommentCount] = useState(0);

  useEffect(() => {
    // Gọi API để lấy số lượng bài viết và bình luận
    const fetchCounts = async () => {
        try {
            const postRes = await api.get('/users/me/articles/count');
            setPostCount(postRes.data.count);
            
            const commentRes = await api.get('/users/me/comments/count');
            setCommentCount(commentRes.data.count);
        } catch (error) {
            console.error("Lỗi khi lấy số lượng bài viết/bình luận:", error);
        }
    };
    if (user) {
        fetchCounts();
    }
  }, [user]);

  // Nếu chưa đăng nhập, chuyển hướng về trang chủ
  if (!user) {
    return <Navigate to="/" />;
  }
  
  // Hàm để định dạng số lượng (hiển thị 99+ nếu lớn hơn 99)
  const formatCount = (count) => count > 99 ? '99+' : count;

  return (
    <div className="card-bg p-6 md:p-10 rounded-lg max-w-4xl mx-auto">
      {/* Phần thông tin cá nhân */}
      <div className="flex flex-col sm:flex-row items-center gap-6 border-b border-main pb-6 mb-6">
        <img 
          src={user.avatar} 
          alt="User Avatar" 
          className="w-32 h-32 rounded-full border-4 border-accent shadow-md"
        />
        <div className="text-center sm:text-left">
          <h1 className="text-3xl font-bold text-main">{user.name}</h1>
          <p className="text-subtle mt-1">{user.email}</p>
          <button 
            onClick={logout}
            className="mt-4 bg-red-500 text-white px-5 py-2 rounded-md font-medium hover:bg-red-600 transition-colors"
          >
            Đăng xuất
          </button>
        </div>
      </div>

      {/* Phần Tab chuyển đổi */}
      <div>
        <div className="flex border-b border-main mb-6">
          <button 
            onClick={() => setActiveTab('posts')}
            className={`px-4 py-2 font-semibold transition-colors ${activeTab === 'posts' ? 'tab-active' : 'tab-inactive'}`}
          >
            Bài viết của tôi ({formatCount(postCount)})
          </button>
          <button 
            onClick={() => setActiveTab('comments')}
            className={`px-4 py-2 font-semibold transition-colors ${activeTab === 'comments' ? 'tab-active' : 'tab-inactive'}`}
          >
            Bình luận của tôi ({formatCount(commentCount)})
          </button>
          <Link 
            to="/messenger" 
            className="px-4 py-2 font-semibold tab-inactive text-accent-hover"
          >
            Lịch sử Tư vấn
          </Link>
        </div>
        
        {/* Nội dung của Tab */}
        <div>
          {activeTab === 'posts' && <MyPosts />}
          {activeTab === 'comments' && <MyComments />}
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
