// src/App.js
import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Sidebar from './components/Sidebar';
import ProtectedRoute from './components/ProtectedRoute';
import HomePage from './pages/HomePage';
import AboutPage from './pages/AboutPage';
import ContactPage from './pages/ContactPage';
import LoginPage from './pages/LoginPage';
import RegisterPage from './pages/RegisterPage';
import ArticleDetailPage from './pages/ArticleDetailPage';
import ProfilePage from './pages/ProfilePage';
import CreatePostPage from './pages/CreatePostPage';
import ForgotPasswordPage from './pages/ForgotPasswordPage';
import KnowledgeDetailPage from './pages/KnowledgeDetailPage'; 
import ChatbotWidget from './components/ChatbotWidget';

function App() {
  const [searchTerm, setSearchTerm] = useState('');

  return (
    <div>
    <Navbar setSearchTerm={setSearchTerm} />
      <div className="container mx-auto flex flex-col md:flex-row gap-8 px-4 py-8">
        <Sidebar />
        <main className="flex-grow w-full md:w-auto">
          <Routes>
            <Route path="/" element={<HomePage searchTerm={searchTerm} />} />
            <Route path="/knowledge/:id" element={<KnowledgeDetailPage />} /> {/* <-- ROUTE MỚI */}
            <Route path="/article/:id" element={<ArticleDetailPage />} />
            <Route path="/about" element={<AboutPage />} />
            <Route path="/contact" element={<ContactPage />} />
            <Route path="/login" element={<LoginPage />} />
            <Route path="/register" element={<RegisterPage />} />
            <Route path="/forgot-password" element={<ForgotPasswordPage />} />
            <Route path="/profile" element={<ProtectedRoute><ProfilePage /></ProtectedRoute>} />
            <Route path="/create-post" element={<ProtectedRoute><CreatePostPage /></ProtectedRoute>} />
          </Routes>
        </main>
      </div>
      <ChatbotWidget />
      {/* KHU VỰC CÁC NÚT HÀNH ĐỘNG NỔI */}
      <div className="fixed bottom-5 right-5 flex flex-col items-center gap-3 z-40">
        <ChatbotWidget />
        <a 
          href="https://www.facebook.com/trien.vo.337115/about_overview" 
          target="_blank" 
          rel="noopener noreferrer"
          className="bg-[#1877F2] w-14 h-14 rounded-full text-white flex items-center justify-center shadow-lg transform transition-transform hover:scale-110"
          aria-label="Facebook của chúng tôi"
        >
          <FaFacebook size={28} />
        </a>
      </div>
    </div>
  );
}

export default App;
