import React, { useState } from 'react';
import api from '../services/api';
import { getUser } from '../services/auth';

export default function Chatbot() {
  const [open, setOpen] = useState(false);
  const [chat, setChat] = useState([]);
  const [input, setInput] = useState('');

  const user = getUser();

  const sendQuestion = async () => {
    const res = await api.post('/chatbot', { user_id: user?.id, question: input });
    setChat([...chat, { q: input, a: res.data.answer }]);
    setInput('');
  };

  return (
    <div>
      <button
        onClick={() => setOpen(!open)}
        className="fixed bottom-6 right-6 bg-blue-500 text-white p-4 rounded-full shadow-lg"
      >
        ðŸ’¬
      </button>
      {open && (
        <div className="fixed bottom-20 right-6 w-80 bg-white shadow-xl border rounded-lg p-3">
          <div className="h-64 overflow-y-auto mb-2">
            {chat.map((c, i) => (
              <div key={i} className="mb-2">
                <p><b>Báº¡n:</b> {c.q}</p>
                <p><b>AI:</b> {c.a}</p>
              </div>
            ))}
          </div>
          <input
            className="border p-2 w-full mb-2"
            placeholder="Nháº­p cÃ¢u há»i..."
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button onClick={sendQuestion} className="bg-blue-500 text-white w-full py-2 rounded">Gá»­i</button>
        </div>
      )}
    </div>
  );
}
