// src/pages/AboutPage.js
import React, { useEffect } from 'react';
// import './AboutPage.css'; // Sẽ dùng CSS từ index.css
import { Link } from 'react-router-dom';

function AboutPage() {
  useEffect(() => {
    window.scrollTo(0, 0); 
  }, []);

  return (
    // Chúng ta sẽ dùng các class từ file index.css (bạn đã cung cấp)
    // và thêm một vài class Tailwind để đảm bảo nó đẹp
    <div className="about-page-content bg-white dark:bg-gray-800 shadow-lg p-6 md:p-10 rounded-lg">
      <h1>Giới thiệu về CarePlus</h1>

      <section className="about-intro">
        <h2>Hệ thống Phòng khám quốc tế CarePlus</h2>
        <p>Hệ thống Phòng khám quốc tế CarePlus được điều hành bởi Công ty TNHH CityClinic Việt Nam. Những thông tin y khoa trên trang web chỉ dùng để tham khảo, bạn nên liên hệ bác sĩ để được tư vấn trước khi có bất cứ hành động nào.</p>
        <Link to="/contact" className="read-more-link-about">Liên hệ với chúng tôi &rarr;</Link>
      </section>

      {/* Phần còn lại của code bạn cung cấp... */}
      {/* ... (dán toàn bộ phần Chính sách hoạt động, Chính sách bảo mật vào đây) ... */}
      <section className="about-section policy-section">
        <h2>Chính sách hoạt động</h2>
        <p>
          Chào mừng Bạn đến với Website Thương mại điện tử của Hệ thống phòng khám Quốc tế CarePlus thuộc Công ty TNHH CityClinic Việt Nam (gọi chung là "Website")...
        </p>
        {/* ... (Toàn bộ nội dung trang Giới thiệu của bạn) ... */}
      </section>

      <section className="about-section policy-section">
        <h2>Chính sách bảo mật</h2>
        {/* ... (Toàn bộ nội dung trang Giới thiệu của bạn) ... */}
      </section>
    </div>
  );
}

export default AboutPage;
