// src/components/ArticleCard.js
import React from 'react';
import { Link } from 'react-router-dom';

const ArticleCard = ({ article }) => {
  const { id, title, excerpt, imageUrl, author, date } = article;
  return (
    <Link to={`/article/${id}`} className="block card-bg rounded-lg overflow-hidden transform hover:-translate-y-1 transition-transform duration-300 group">
        <div className="overflow-hidden">
          <img className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300" src={imageUrl} alt={title} />
        </div>
        <div className="p-5">
          <h3 className="font-bold text-lg mb-2 text-main group-hover:text-accent transition-colors">{title}</h3>
          <div className="text-xs text-subtle mb-3">
            <span>{author} | {date}</span>
          </div>
          <p className="text-sm mb-4 text-subtle leading-relaxed">{excerpt}</p>
          <span className="font-semibold text-accent text-accent-hover">Xem chi tiết →</span>
        </div>
    </Link>
  );
};

export default ArticleCard;
