// src/components/Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { useTheme } from '../context/ThemeContext';
// Import các icon cần thiết
import { FaSun, FaMoon, FaLeaf, FaGlobe } from 'react-icons/fa';

const Navbar = () => {
  const { user, logout } = useAuth();
  const { theme, setTheme } = useTheme();
  const [isLangMenuOpen, setIsLangMenuOpen] = useState(false);
  // Giả sử có state cho ngôn ngữ, sẽ hoàn thiện ở bước sau
  const [language, setLanguage] = useState('VIETNAMESE'); 

  const handleThemeChange = () => {
    if (theme === 'theme-light') {
      setTheme('theme-dark');
    } else if (theme === 'theme-dark') {
      setTheme('theme-green');
    } else {
      setTheme('theme-light');
    }
  };

  const handleLanguageChange = (lang) => {
    setLanguage(lang);
    setIsLangMenuOpen(false); // Đóng menu sau khi chọn
  };

  return (
    <header className="bg-white dark:bg-gray-800 shadow-md sticky top-0 z-50 transition-colors duration-300">
      <div className="container mx-auto px-4 py-3 flex justify-between items-center">
        
        {/* Phần 1: Logo & Tên trang */}
        <div className="flex items-center gap-3">
          <Link to="/" className="flex items-center gap-3">
            {/* THAY ĐỔI LOGO Ở ĐÂY */}
            <img 
              src="https://ttagencyads.com/wp-content/uploads/2022/03/Logo-rong-do.jpg" 
              alt="CarePlus Logo" 
              className="w-10 h-10 rounded-full object-cover"
            />
            <div>
              <h1 className="text-xl font-bold text-blue-600 dark:text-blue-400">CarePlus</h1>
              <span className="text-xs text-gray-500 dark:text-gray-400 hidden sm:block">
                Thông tin & Kiến thức Y khoa
              </span>
            </div>
          </Link>
        </div>

        {/* Phần 2: Thanh điều hướng (NavBar) */}
        <nav className="hidden md:flex items-center gap-6">
          <Link to="/" className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600 dark:hover:text-blue-400">Trang chủ</Link>
          <Link to="/about" className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600 dark:hover:text-blue-400">Về chúng tôi</Link>
          <Link to="/" className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600 dark:hover:text-blue-400">Kiến thức</Link>
          <Link to="/contact" className="font-medium text-gray-700 dark:text-gray-200 hover:text-blue-600 dark:hover:text-blue-400">Liên hệ</Link>
        </nav>

        {/* Phần 3: Các nút hành động (Actions) */}
        <div className="flex items-center gap-3">
          {/* Theme Switcher - LOGIC ĐÃ SỬA */}
          <button onClick={handleThemeChange} className="p-2 rounded-full text-gray-700 dark:text-gray-200 hover:bg-gray-200 dark:hover:bg-gray-700" title="Đổi giao diện">
            {theme === 'theme-light' && <FaMoon size={18} />}
            {theme === 'theme-dark' && <FaLeaf size={18} />}
            {theme === 'theme-green' && <FaSun size={18} />}
          </button>
          
          {/* Language Toggle - CÓ DROPDOWN */}
          <div className="relative">
            <button onClick={() => setIsLangMenuOpen(!isLangMenuOpen)} className="p-2 rounded-full text-gray-700 dark:text-gray-200 hover:bg-gray-200 dark:hover:bg-gray-700 hidden sm:block" title="Ngôn ngữ">
              <FaGlobe size={18} />
            </button>
            {isLangMenuOpen && (
              <div className="absolute right-0 mt-2 w-40 bg-white dark:bg-gray-700 rounded-md shadow-lg py-1">
                <button onClick={() => handleLanguageChange('VIETNAMESE')} className="block w-full text-left px-4 py-2 text-sm text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-600">VIETNAMESE</button>
                <button onClick={() => handleLanguageChange('ENGLISH')} className="block w-full text-left px-4 py-2 text-sm text-gray-700 dark:text-gray-200 hover:bg-gray-100 dark:hover:bg-gray-600">ENGLISH</button>
              </div>
            )}
          </div>

          {/* Login / User Info - HIỂN THỊ AVATAR */}
          {user ? (
            <div className="flex items-center gap-3 ml-2">
              <span className="font-semibold text-gray-700 dark:text-gray-200 hidden sm:inline">
                {user.name}
              </span>
              {/* Giả sử backend trả về user.avatar */}
              <img 
                src={user.avatar || `https://ui-avatars.com/api/?name=${user.name}&background=random`} 
                alt="User Avatar" 
                className="w-9 h-9 rounded-full border-2 border-blue-500"
              />
              <button onClick={logout} className="bg-red-500 text-white px-3 py-1.5 rounded-md font-medium hover:bg-red-600 transition-colors text-sm">
                Thoát
              </button>
            </div>
          ) : (
            <a href="http://localhost:3001/api/auth/google" className="bg-blue-600 text-white px-4 py-2 rounded-md font-medium hover:bg-blue-700 transition-colors">
              Đăng nhập
            </a>
          )}
        </div>
      </div>
    </header>
  );
};

export default Navbar;
